function initBurgerMenu(){document.addEventListener("DOMContentLoaded",()=>{let t=document.querySelector(".header__burger"),s=document.querySelector(".header__nav"),c=document.body,a=document.querySelectorAll(".button__header");t.addEventListener("click",e=>{e.stopPropagation(),t.classList.toggle("active"),s.classList.toggle("active"),a.forEach(e=>{e.classList.add("_anim"),setTimeout(()=>e.classList.remove("_anim"),2400)}),t.classList.contains("active")?c.classList.add("no-scroll"):c.classList.remove("no-scroll")}),document.addEventListener("click",e=>{s.contains(e.target)||t.contains(e.target)||s.classList.contains("active")&&(t.classList.remove("active"),s.classList.remove("active"),c.classList.remove("no-scroll"))})})}export{initBurgerMenu};